mapper.update:
update activity
title = #{title, jdbcType=VARCHAR}, site = #{site, jdbcType=VARCHAR}, number_people = #{number_people, jdbcType=INTEGER}, deadline = #{deadline, jdbcType=TIMESTAMP}, introduction = #{introduction, jdbcType=VARCHAR}, time_start = #{time_start, jdbcType=TIMESTAMP}, time_end = #{time_end, jdbcType=TIMESTAMP}, start = #{start, jdbcType=TIMESTAMP},
where organizer = #{organizer, jdbcType=INTEGER} AND id = #{id, jdbcType=INTEGER}

update activity
set already_register = already_register + 1
where id = #{actid, jdbcType=INTEGER}

mapper.delete:
delete from useractivity
where userid=#{userid, jdbcType=INTEGER}
 AND actid=#{actid, jdbcType=INTEGER}

mapper.update:
update activity
set already_register = already_register - 1
where id = #{actid, jdbcType=INTEGER}
